import millisecondsToDuration from"./milliseconds_to_duration";let DAY_IN_MILLISECONDS=864e5,HOUR_IN_MILLISECONDS=36e5,MINUTE_IN_MILLISECONDS=6e4,SECOND_IN_MILLISECONDS=1e3,UNIT_TO_MILLISECOND_CONVERT={ms:1,s:SECOND_IN_MILLISECONDS,min:MINUTE_IN_MILLISECONDS,h:HOUR_IN_MILLISECONDS,d:DAY_IN_MILLISECONDS},formatDuration=(I,N)=>millisecondsToDuration(parseFloat(I)*UNIT_TO_MILLISECOND_CONVERT[N])||"0";export{UNIT_TO_MILLISECOND_CONVERT,formatDuration};